mapper.update:
update orders
cancel_reason=#{cancelReason}, rejection_reason=#{rejectionReason}, cancel_time=#{cancelTime}, pay_status=#{payStatus}, pay_method=#{payMethod}, checkout_time=#{checkoutTime}, status = #{status}, delivery_time = #{deliveryTime}
where id = #{id}

mapper.select:
select * from orders


        and user_id=#{userId}


        and status=#{status}

SELECT o.id, o.number, o.status, o.user_id, o.address_book_id, o.order_time, o.checkout_time, o.pay_method, o.pay_status, o.amount, o.remark, o.user_name, o.phone, o.address, o.consignee, od.id as detail_id, od.name as detail_name, od.image as detail_image, od.order_id, od.dish_id, od.setmeal_id, od.dish_flavor, od.number as detail_number, od.amount as detail_amount FROM orders o LEFT JOIN order_detail od ON o.id = od.order_id
AND o.user_id = #{userId}
AND o.status = #{status}
ORDER BY o.order_time DESC

select * from orders


          and order_time >=#{beginTime}


          and order_time <= #{endTime}



        and  number=#{number}


        and phone=#{phone}


        and status =#{status}


order by order_time desc

select sum(amount) from orders


        and order_time > #{begin}


        and order_time < #{end}


        and status = #{status}

select COUNT(id) from orders


        and order_time > #{begin}


        and order_time < #{end}


        and status = #{status}

SELECT od.name, SUM(od.number) AS number FROM order_detail od, orders o WHERE od.order_id = o.id AND o.status = 5
AND o.order_time > #{begin}
AND o.order_time < #{end}
GROUP BY od.name ORDER BY number DESC LIMIT 0, 10
